const initial_state = {
	balance: 0
};

const reducer = (state = initial_state, action) =&gt; {
	switch(action.type){
		case "Deposit":
			return {balance: state.balance + action.payload};
		case "Withdraw":
			return {balance: state.balance - action.payload};
		default:
			return state;
	}
};

export default reducer;
